import React, {Fragment} from 'react';

const Posts = ({ posts }) => { // by putting our posts as our parameter, it destructures our posts and sets it as a prop

    return (
        <div className='postBody'>
            {
                posts.map((post) => {
                    const { description, location, title, price, _id } = post;
                    return (
                        <div className='postContainer' key={_id}>
                            <div className='postContainer1'>
                                <h3>{title}</h3>
                                <p className='postings'>Description: {description}</p>
                                <p className='postings'>Price: {price}</p>
                                <p className='postings'>Location: {location}</p>
                            </div>
                        </div>
                    )
                })
            }
        </div>
    )
}

export default Posts;
