import React from 'react';

const Posts = ({ posts }) => { // by putting our posts as our parameter, it destructures our posts and sets it as a prop

    return (
        <div>
        {
            posts.map((post) =>{
                const {description, location, title, price} = post;
                return (
                    <>
                        <h3>{title}</h3>
                        <p>{description}</p>
                    </> 
                )
            })
        }
        </div>
    )
}

export default Posts;
