import React, {useState} from 'react';
import { createPost } from '../api';

const CreatePost = ({ token }) => {
    const [title, setTitle] = useState('')
    const [description, setDescription] = useState('')
    const [price, setPrice] = useState('')
    const [location, setLocation] = useState('')
    const [willDeliver, setWillDeliver] = useState(false)

    const newPost = {
        title,
        description,
        price,
        location,
        willDeliver
    }

    async function addPost() {
        const result = await createPost(token, newPost)
        console.log(result)
    }

    return (
        // this needs to be a form that accepts the five requests parameters
        <form className='createNewPost'>
            <label>Title</label>
            <input
            className='postings1'
                type='text'
                onChange={(event) => setTitle(event.target.value)} />
            <label>Description</label>
            <input
            className='postings1'
                type='text'
                onChange={(event) => setDescription(event.target.value)} />
            <label>Price</label>
            <input
            className='postings1'
                type='text'
                onChange={(event) => setPrice(event.target.value)} />
            <label>Location</label>
            <input
            className='postings1'
                type='text'
                onChange={(event) => setLocation(event.target.value)} />
            <label>Delivery</label>
            <input
            className='postings1'
                type='checkbox'
                id='Yes'
                onChange={(event) => setWillDeliver(event.target.value)} />
            <button onClick={(event) => { addPost(); event.preventDefault() }}>Create a New Post</button>
        </form>
    )
}

export default CreatePost;
