import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom/client';
import { Route, BrowserRouter, Routes, useNavigate} from 'react-router-dom';
import { getPosts, getUserDetails } from './api';
import './style.css'

import {
    Navbar,
    Posts,
    Profile,
    Home,
    Register,
    Login
} from './components'

const App = () => {
    const [posts, setPosts] = useState([]);
    const [token, setToken] = useState('');
    const navigate = useNavigate();

    // console.log(token) 
    // can log the token of login if interested in seeing it

    function logout() {
        window.localStorage.removeItem('token');
        setToken('')
    }

    async function fetchPosts() {
        const results = await getPosts()
        setPosts(results.data.posts);
    }

    function getMe() {
        const storedToken = window.localStorage.getItem('token')
        if (!token) {
            setToken(storedToken);
            return;
        }
        getUserDetails(token)
    }

    useEffect(() => {
        fetchPosts()
    }, []) 

    useEffect(() => {
        getMe();
    }, [token])

    return (
        <div className='navbarLinks'>
            <Navbar logout={ logout }/>
            <Routes>
                <Route path='/' element={<Home />} />
                <Route path='/posts' element={<Posts posts={posts}/>} />
                <Route path='/profile' element={<Profile />} />
                <Route path='/register' element={<Register setToken={setToken} token={token} navigate={navigate}/>} />
                <Route 
                    path='/login'
                    element={ <Login 
                        setToken={ setToken }
                        navigate={ navigate}
                    />}
                    />
            </Routes>
        </div>
    )
}

const container = document.querySelector('#container');
const root = ReactDOM.createRoot(container);
root.render(
    <BrowserRouter>
        <App />
    </BrowserRouter>
);


/*

Login
(LocalStorage)
    - setItem
        - sets kkey/value pair items
        - window.localStorage.setItem('token', <token>)
    - getItem
        - gets value based on item name
        - window.localStorage.getItem('token')
    - removeitem
        - removes key/value pair in localStorage
        - window.localStorage.removeItem('token')
