import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import { deletePost } from '../api';

const Profile = ({user, token}) => {
    const {messages, posts, username} = user;
    let postContainer = document.getElementById('postContainer')

    return (
        <div>
            <h1>Welcome, {username}!</h1>
            <div className='profilePosts'>
                <h3>Active Posts: </h3>
                <button className='addAPost'><Link to='/posts/create-post'>Add a Post</Link></button>
            </div>
            <div className='postBody'>
                {
                    posts.map((post) => {
                        const { description, location, title, price, _id, isAuthor, active } = post;
                        return (
                            <div className='postContainer' id='postContainer' key={_id}>
                                {
                                    active ? (
                                        <>
                                            <div className='postContainer1'>
                                                <h3>{title}</h3>
                                                <p className='postings'>Description: </p><p>{description}</p>
                                                <p className='postings'>Price: </p><p className='postingInfo'>{price}</p>
                                                <p className='postings'>Location: </p><p className='postingInfo'>{location}</p>
                                            </div>
                                            <button className='deletePost' onClick={() => deletePost(token, _id)}>Delete</button>
                                            <p>This post is active.</p>
                                        </>
                                    ) : (
                                        <div className='doNotShow'></div>
                                    )
                                }

                            </div>
                        )
                    })
                }
            </div>
        </div>
    )
}

export default Profile;
