import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { createMessage } from '../api';

const SendMessage = ({ postID, token }) => {
    const [message, setMessage] = useState({ content: '' });
    // we need 3 things to make this request
    // Post-id, token, message object containing the content of the message

    async function addMessage() {
        await createMessage({ postID, message, token })
    }

    return (
        <form
            id='messageForm'
            className='messageForm'
            onSubmit={(ev) => {
                ev.preventDefault();
                addMessage();
            }}>
            <input
                className='messageInput'
                type='text'
                placeholder='Enter Message'
                onChange={(ev) => setMessage({ content: ev.target.value })}
            />
            <button type='submit' className='messageSubmit' onClick={() => {navigate('/profile')}}>Send</button>
        </form>
    )
}

const SinglePostView = ({ posts, token, navigate }) => {
    const [activateMessage, setActivateMessage] = useState(false);

    const { postID } = useParams();

    if (posts.length) {
        const [currentPost] = posts.filter(post => post._id === postID);
        const { title, description, location, price, willDeliver } = currentPost;

        return (
            <div className='singlePostViewBody'>
                <div className='singlePostView' >
                    <div className='postContainer1'>
                        <h3 className='SPVTitle'>{title}</h3>
                        <p className='SPVContent'>Description: </p><p className='postingInfo1'>{description}</p>
                        <p className='SPVContent'>Price: </p><p className='postingInfo1'>{price}</p>
                        <p className='SPVContent'>Location: </p><p className='postingInfo1'>{location}</p>
                    </div>
                    <div className='buttonContainer1'>
                        <button id='contactSeller' onClick={() => { setActivateMessage(!activateMessage); contactSeller.style.display = 'none' }}>Message Seller</button>
                        {
                            activateMessage && <SendMessage postID={postID} token={token} navigate={navigate} />
                        }
                    </div>
                </div>
            </div>
        )
    } else {
        return (
            <h1>Post is loading...</h1>
        )
    }
}

export default SinglePostView;
