import React from 'react';
import { useParams } from 'react-router-dom';

const SendMessage = () => {
    return (
        <div>SEND MESSAGE</div>
    )
}

const SinglePostView = ({ posts }) => {
  const { postID } = useParams();
  
  const [currentPost] = posts.filter(post => post._id === postID);
  
  const {title, description, location, price, willDeliver} = currentPost;
  
  return (
      <div className='singlePostView' >
          <div className='postContainer1'>
              <h3 className='SPVTitle'>{title}</h3>
              <p className='postings1'>Description: </p><p className='postingInfo1'>{description}</p>
              <p className='postings1'>Price: </p><p className='postingInfo1'>{price}</p>
              <p className='postings1'>Location: </p><p className='postingInfo1'>{location}</p>
              <div className='buttonContainer'><button id='contactSeller'>Contact Seller</button></div>
          </div>
      </div>
  )
}

export default SinglePostView;
